cat All.RAW.txt | sed  "s/\"//g" | grep -v "total_count: 0" | sed 's/description:/\tdescription:/1;s/,gene_id:/\tgene_id:/1;s/,protein_count:/\tprotein_count:/1;s/,symbol:/\tsymbol:/1;s/,tax_id:/\ttax_id:/1;s/,taxname:/\ttaxname:/1;s/,transcript_count:/\t/1' | cut -f 2- | sed 's/,cds:/\tcds:/1' | cut -f 1-6,8- | sed "s/:{accession_version:/:/1;s/\[//g;s/\]//g" |  sed "s/:{begin:/:/1" | sed 's/}},/\t/1;s/,end:/___/1' | sed 's/genomic_accession_version:/\tgenomic_accession_version:/1' | sed "s/:{accession_version:/:/1;s/\[//g;s/\]//g" |  sed "s/:{begin:/:/1" | sed 's/}},/\t/1;s/,end:/___/1' | sed 's/genomic_accession_version:/\tgenomic_accession_version:/1' | cut -f 1-7,9- | sed "s/:{begin:/:/1;s/,end:/___/1" | sed "s/},sequence_name/\tsequence_name/1" | sed 's/},/\t/1' | sed 's/,query:/\t/1;s/},total_count:/\t/g' |  awk -F "\t" '{print $2"\t"$(NF-1)"\t"$4"\t"$1"\t"$3"\t"$5"\t"$6"\t"$7"\t"$8"\t"$9"\t"$10}' | sed 's/description://g;s/symbol://g;s/gene_id://g;s/protein_count://g;s/tax_id://g;s/taxname://g;s/cds://g;s/genomic_accession_version://g;s/sequence_name://g;s/\t\t/\t/g' | sed "s/ /________/g" | csvtk add-header -t -n gene_id,pep_id,symbol,description,Alternative_splicing_count,tax_id,species,cds_infomation,chromosome_information,chromosome_information2 | sed 's/________/ /g' > All.tsv
